import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';


export interface Dvd{
  name : string,
  genre : string,
  quantity : number,
  prix: number;
  photo: string;
  description: string;
  id? : number,
}

@Injectable({
  providedIn: 'root'
})
export class NewdvdService {

  constructor(private http: HttpClient) { }

  getDvdById(id: number): Observable<Dvd[]>{
    return this.http.get<Dvd[]>(`http://localhost:80/dvds/${id}`);
  }

  updateById(id: number, value:any ): Observable<any>{ 
    return this.http.put(`http://localhost:80/dvds/${id}`, value);
  }

  delete(id: number): Observable<Dvd[]>{
    return this.http.delete<Dvd[]>(`http://localhost:80/dvds/delete/${id}`);
  }
}
