import { Component, OnInit, OnChanges } from '@angular/core';
import { NewdvdService } from '../services/newdvd.service';
import { ActivatedRoute, Router } from '@angular/router';
import { filter } from 'rxjs';

export interface Dvd{  
  name : string,
  genre : string,
  quantity : number,
  prix: number; 
  photo: string;
  description: string;
  pathPhoto: string;
  id? : number,
}

@Component({
  selector: 'app-newdvd',
  templateUrl: './newdvd.component.html',
  styleUrls: ['./newdvd.component.css']
})

export class NewdvdComponent implements OnInit {

  dvd: any = [];
  detail: number=0;

  constructor(private router: Router, private newDvdService: NewdvdService,private route:ActivatedRoute) { 

    this.router.events.subscribe( (val: any) => {
      if(val.url){
      var id = val.url.split('=')[1];
      this.detail=id;
      }});
  }

  ngOnInit(){
    this.newDvdService.getDvdById(this.detail).subscribe(data=> {
      data.pathPhoto= data.pathPhoto ? data.pathPhoto.replace(/\\/g, '/') as string : undefined
      this.dvd = data;
    })
  }

  filterById(){
  this.route.params.subscribe(params => {
    const id = params['id'];
    if(this.router.url.endsWith(id)){
      this.filterDvdsById(id);
    } else {
      "aucun dvd ne correspond à cet id"
  }
  });}

  filterDvdsById(id: number) {
    this.dvd = this.dvd.filter((dvd: Dvd) => dvd.id === id);
  }

updateById(id: number){
  this.router.navigate(['/dvds',id]);
  console.log(this.dvd);
}


deleteDvd(id: number) {
  this.newDvdService.delete(id)
  .subscribe(data => {
    console.log(data);
    this.ngOnInit();
  });
}



}

<div class="absolute h-124 flex flex-wrap  justify-between w-9/12  ml-80 top-64 overflow-hidden overflow-scroll ">
    <section class="max-w-4xl p-4 mx-auto bg-fumee rounded-md shadow-md dark:bg-gray-800 mt-4 mb-10">
        <h1 class="text-xl font-bold text-basic">Ajout New DVD</h1>
        <form>
            <div>
                    <p *ngIf="getId" class="block w-1/5 px-4 py-2 mt-2 text-basic bg-fumee rounded-full focus:border-blue-500  focus:outline-none focus:ring">Id: {{dvd.id}}</p>
                </div>
            <div class="grid grid-cols-1 gap-4 mt-4 sm:grid-cols-2">
                <div>
                    <label class="text-basic" for="username">Titre</label>
                    <input id="name" type="text" placeholder="{{dvd.name}}" value="{{dvd.name}}" class="block w-full px-2 py-2 mt-2 text-basic bg-fumee border border-gray-300 rounded-full focus:border-blue-500  focus:outline-none focus:ring">
                </div>

                <div>
                    <label class="text-basic " for="emailAddress">Genre</label>
                    <input id="genre" type="text" placeholder="{{dvd.genre}}" value="{{dvd.genre}}" class="block w-full px-4 py-2 mt-2 text-basic bg-fumee border border-gray-300 rounded-full  focus:border-blue-500 focus:outline-none focus:ring">
                </div>

                <div>
                    <label class="text-basic" for="password">Prix</label>
                    <input id="prix" type="text" placeholder="{{dvd.prix}}" value="{{dvd.prix}}" class="block w-full px-4 py-2 mt-2 text-basic bg-fumee border border-gray-300 rounded-full focus:border-blue-500  focus:outline-none focus:ring">
                </div>

                <div>
                    <label class="text-basic" for="passwordConfirmation">Quantité</label>
                    <input id="quantity" type="text" placeholder="{{dvd.quantity}}" value="{{dvd.quantity}}"  class="block w-full px-4 py-2 mt-2 text-basic bg-fumee border border-gray-300 rounded-full focus:border-blue-500 focus:outline-none focus:ring">
                </div> 

            </div>
                <div class="flex flex-col w-full">
                    <div class="w-100 pt-4 pb-4" >
                        <label class="text-basic" for="passwordConfirmation">Description</label>
                        <textarea id="description" placeholder="{{dvd.description}}" value="{{dvd.description}}" type="text" class="block w-full row-4 px-4 py-2 mt-2 text-basic bg-fumee border border-gray-300 rounded-md focus:border-blue-500 focus:outline-none focus:ring"></textarea>
                    </div>
                    <div class="w-full flex flex-col m-auto justify-center">
                        <label class="block text-sm font-medium text-basic">
                        Photo</label>
                        <div class="flex">
                        <img class="object-cover w-1/2" src="{{dvd.photo}}" alt="image jacket">
                        <div class="mt-1 flex justify-center  item-center m-auto px-4 pt-5 pb-4 border-2 border-gray-300 border-dashed rounded-md">


                            <div class="space-y-1 text-center">
                                <svg class="mx-auto h-12 w-12 text-basic" stroke="currentColor" fill="none" viewBox="0 0 48 48" aria-hidden="true">
                                <path d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" />
                                </svg>
                                <div class="flex text-sm text-gray-600">
                                    <label for="file-upload" class="relative cursor-pointer bg-fumee rounded-md font-medium text-indigo-600 hover:text-indigo-500 focus-within:outline-none focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-indigo-500">
                                    <span class="">Upload a file</span>
                                    <input id="photo" name="photo" type="file" class="sr-only"></label>
                                    <p class="pl-1 text-basic">or drag and drop</p>
                                </div>
                                <p class="text-xs text-basic">PNG, JPG, JPEG up to 10MB</p>
                            </div>
                        </div>
                    </div>
                    </div>
                </div>

            <div class="flex justify-around mt-6">  
                <a href=""><button id="delete" class="px-6 py-2 leading-5 text-fumee transition-colors duration-200 transform bg-red-400 rounded-md hover:bg-red-600 focus:outline-none" >Supprimer</button></a>

              <button (click)="updateById(dvd.id)" id="ajouterDVD" class="px-6 py-2 leading-5 text-fumee transition-colors duration-200 transform bg-granny-400 rounded-md hover:bg-granny-600 focus:outline-none" >Ajouter</button>

            </div>
        </form>
    </section>
</div>
