import { Component } from '@angular/core';
import { AuthService } from '../services/auth.service';
import { Router } from '@angular/router';
import { NgForm } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

export interface Owner{

  username: string;
  password: string;

}
export interface AuthResponseDto{
  token: string;
}

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
user: any = [];
  authResponseDto: any | undefined;


  constructor(private authService: AuthService, private router: Router , private http: HttpClient ) {}

  send = (form: NgForm) =>{
    if(form.valid){
      console.log(form.value);

     this.authResponseDto= this.authService.send(form.value);
     console.log(this.authResponseDto);
     sessionStorage.setItem('token', this.authResponseDto.token);
      this.router.navigate(['/home']); 
      console.log(this.authResponseDto.token);
    }
  }

}
